document.addEventListener('DOMContentLoaded',()=>{initModalSetup()});function initModalSetup(){let modal_btns=document.querySelectorAll('.js-toggle-modal');modal_btns.forEach(btn=>{btn.addEventListener('click',(e)=>{e.preventDefault();let modal_id=btn.getAttribute('data-target-modal');let modal=document.getElementById('vlx-'+modal_id);initModal(modal,btn)})})}
async function initModal(modal,btn){openModal(modal);let close_btn=modal.querySelector('.js-close-modal');if(close_btn){close_btn.addEventListener('click',(e)=>{e.preventDefault();closeModal(modal)})}else{console.log('No close button found for modal, skipping click event listener')}
modal.addEventListener('click',(e)=>{e.preventDefault();if(e.target===modal){closeModal(modal)}})}
function openModal(modal){document.querySelector('html').classList.add('--no-scroll');modal.classList.add('--active')}
function closeModal(modal){modal.classList.remove('--active');document.querySelector('html').classList.remove('--no-scroll')}